var fs     = require("fs");
exports.do = function(user,serial) {
	fs.readFile("./html/"+serial+"/info.json", "utf8", function(err,data) {
		if(err) {
			console.log(err);
			throw err;
		}
		else {
			var info=JSON.parse(data);
			console.log(info);
			info.versions.push(user);
			fs.writeFile("./html/"+serial+"/info.json", JSON.stringify(info));
			fs.mkdir("./html/"+serial+"/"+user,function(err,data){
				var path="./html/"+serial+"/"+user+"/";
				fs.writeFile(path+"tools.json","{}");
				fs.mkdir(path+"issues");
				fs.mkdir(path+"comments");
				fs.writeFile(path+"issues.txt","");	
			});
		}
	});
}